import React, { Component } from 'react';
import {connect} from 'react-redux'

import store from './store/index'

class TodoList extends Component {
    constructor(props){
        super(props);
        this.state = store.getState();
    }
    render() { 
        // 4 在该组件中使用state中的属性的地方进行修改，直接从props中取值即可
        const {inputValue, inputChange,onClick} = this.props;
        return ( 
            <div>
                <div>
                    <input value={inputValue} onChange={inputChange}/>
                    <button>提交</button>
                </div>
                <ul>
                    <li>JSpang</li>
                </ul>
            </div>
        );
    }
}

// 3 定义映射关系函数1，将store对象中的state映射为组件TodoList中的props属性
const stateProps = (state) => {
    // console.log(state);   这里传入的state就是store仓库中的state
    return {inputValue: state.inputValue};
}

// 定义映射关系函数2
const dispatchToProps = (dispatch) => {
    return {
        inputChange(e){
            let action = {
                type: 'change_input',
                value: e.target.value
            }
            dispatch(action);
        },
        onClick(){
            let action = {
                type: 'add_items'
            }
            dispatch(action);
        }
    }
}

// 2 改变暴露的组件，将暴露的内容改为connect连接器
export default connect(
    stateProps,
    dispatchToProps)(TodoList);
